import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck } from "@fortawesome/free-solid-svg-icons";

const TaskInput = ({addTask, tasks}) => {
  const [value, setValue] = useState("");
  const [completedTasks, setCompletedTasks] = useState(tasks.every(task => task.category === 'complited'))

  const handleOnChange = (e) => {
    setValue(e.target.value);
  };

  const handleOnKeyPress = (e) => {
    if (e.key == "Enter") {
      addTask(value);
      setValue('')
    }
  };
  
  return (
    <div className="inputTask">
      <FontAwesomeIcon icon={faCheck} className={`icon ${completedTasks ? 'iconActive' : ''}`}/>
      <Input
        value={value}
        onChange={handleOnChange}
        onKeyPress={handleOnKeyPress}
      />
    </div>
  );
};

export default TaskInput;

const Input = ({value, onChange, onKeyPress }) => {
  return (
    <input
      placeholder="What needs to be done?"
      value={value}
      onChange={onChange}
      onKeyPress={onKeyPress}
    />
  );
};
