import React from "react";

const Categories = (props) => {
    const items = props.items;
    const category = props.category;
    return (
        <div className="categories">
            <p>{`${items} ${items === 1 ? 'item' : 'items'} left`}</p>
            <div>
                <span className={category === 'all' && 'active'} onClick={() => props.onClick('all')}>All</span>
                <span className={category === 'active' && 'active'} onClick={() => props.onClick('active')} >Active</span>
                <span className={category === 'complited' && 'active'} onClick={() => props.onClick('complited')} >Completed</span>
            </div>
        </div>
    )
}

export default Categories
